import {Box, Button, Grid} from "@mui/material";
import CountryCard from "../CountryCard/CountryCard";

const CountryGrid = ({countries, onDelete, onAdd, onEdit}) => {


    return (
        <Grid container spacing={{ xs: 2, md: 3 }}>
            {countries.map((country) => (
                <>
                    <Box sx={{display: "flex", justifyContent: "flex-end", mb: 2}}>
                        <Button variant="contained" color="primary" onClick={() => setAddProductDialogOpen(true)}>
                            Add Product
                        </Button>
                    </Box>
                    <Grid key={country.id} size={{ xs: 12, sm: 6, md: 4, lg: 3 }}>
                        <CountryCard country={country} onDelete={onDelete} onEdit={onEdit}/>
                    </Grid>
                </>

            ))}
        </Grid>
    );
};

export default CountryGrid
