import userRepository from "../../../../repository/userRepository";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { Box, Button, TextField, Typography, Paper } from "@mui/material";
import useUsers from "../../../../hooks/useUsers";

const Login = () => {
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");
    const navigate = useNavigate();
    const {setLoginState,login} = useUsers()

    const handleLogin = async () => {
        login(username,password)

        // await userRepository.login(username, password);
        // const token = localStorage.getItem("jwtToken");
        // if (token) {
        //     console.log("Login successful:", token);
        //     navigate("/books", { replace: true });
        // }
    };

    return (
        <Box sx={{ display: "flex", justifySelf: "center",justifyContent: "center", alignItems: "center", height: "70vh", backgroundColor: "#f0f0f0" }}>
            <Paper sx={{ padding: 4, width: 400, boxShadow: 2 }}>
                <Typography variant="h5" mb={2} textAlign="center">Login</Typography>
                <Box component="form" sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
                    <TextField
                        type="text"
                        label="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        fullWidth
                    />
                    <TextField
                        type="password"
                        label="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        fullWidth
                    />
                    <Button variant="contained" color="primary" onClick={handleLogin} fullWidth>Login</Button>
                </Box>
            </Paper>
        </Box>
    );
};

export default Login;
