import React from 'react';
import { Link } from "react-router-dom";
import { AppBar, Box, Button, Toolbar, Typography } from "@mui/material";
import useUsers from "../../../../hooks/useUsers";
import "./Header.css";

const pages = [
    { path: "/", name: "home" },
    { path: "/books", name: "books" },
    { path: "/authors", name: "authors" },
    { path: "/countries", name: "countries" }
];

const Header = () => {
    const { loggedInUser, logout } = useUsers();

    return (
        <Box>
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" sx={{ mr: 3 }}>E-LIBRARY</Typography>
                    {loggedInUser && (
                        <Box sx={{ flexGrow: 1, display: { xs: "none", md: "flex" } }}>
                            {pages.map((page) => (
                                <Link key={page.name} to={page.path} style={{ textDecoration: "none" }}>
                                    <Button sx={{ color: "white" }}>{page.name}</Button>
                                </Link>
                            ))}
                        </Box>
                    )}
                    <Link to={loggedInUser ? "#" : "/user/login"} style={{ textDecoration: 'none' }}>
                        <Button
                            onClick={loggedInUser ? logout : null}
                            sx={{ color: "white", ml: "auto" }}
                        >
                            {loggedInUser ? "Logout" : "Login"}
                        </Button>
                    </Link>
                </Toolbar>
            </AppBar>
        </Box>
    );
};

export default Header;
