import React, {useEffect, useState} from 'react';
import { Link } from "react-router-dom";
import { AppBar, Box, Button, Toolbar, Typography } from "@mui/material";
import useUsers from "../../../../hooks/useUsers";
import "./Header.css";

const pages = [
    { "path": "/", "name": "home" },
    { "path": "/books", "name": "books" },
    { "path": "/authors", "name": "authors" },
    { "path": "/countries", "name": "countries" },
];

const Header = () => {
    const { loginState,loggedInUser, logout } = useUsers();
    useEffect(() => {
        console.log("Login state in Header:", loginState);
    }, [loginState]);
    return (
        <Box>
            <AppBar position="static">
                <Toolbar>
                    <Typography variant="h6" component="div" sx={{ mr: 3 }}>
                        E-LIBRARY
                    </Typography>
                    {loginState === true  && (
                        <Box sx={{ flexGrow: 1, display: { xs: "none", md: "flex" } }}>
                            {pages.map((page) => (
                                <Link key={page.name} to={page.path} style={{ textDecoration: "none" }}>
                                    <Button sx={{ my: 2, color: "white", display: "block" }}>
                                        {page.name}
                                    </Button>
                                </Link>
                            ))}
                        </Box>
                    )}

                    {loginState ? (
                        <Button
                            onClick={logout}
                            sx={{ my: 2, color: "white", display: "block" }}
                        >
                            Logout
                        </Button>
                    ) : (
                        <Link to="/user/login" style={{ textDecoration: 'none' }}>
                            <Button sx={{ my: 2, color: "white", display: "block" , marginLeft : "145vh" }}>
                                Login
                            </Button>
                        </Link>
                    )}
                </Toolbar>
            </AppBar>
        </Box>
    );
};

export default Header;
